package com.chapter11future;import java.util.concurrent.TimeUnit;/** * Created by wangwenchao02 on 2017/8/28. */public enum Discount {    NONE(0),    SILVER(5),    GOLD(10),    PLATINUM(15),    DIAMOND(20);    private final int percentage;    Discount(int percentage) {        this.percentage = percentage;    }    public static String applyDiscount(Quote quote) {        return quote.getShopName() + " price is " + Discount.apply(quote.getPrice(), quote.getDiscount());    }    private static double apply(double price, Discount discount) {        try {            TimeUnit.SECONDS.sleep(2);        } catch (InterruptedException e) {            e.printStackTrace();        }        return price * (100 - discount.percentage) / 100;    }}