package com.chapter11future;import java.util.ArrayList;import java.util.Arrays;import java.util.List;import java.util.concurrent.CompletableFuture;import java.util.concurrent.ExecutionException;import java.util.concurrent.Future;/** * Created by wangwenchao02 on 2017/8/28. */public class Main {    private static List<String> product = Arrays.asList("aaa", "bbb", "cc");    public static void main(String[] args) {        double price;        Shop shop = new Shop();        price = shop.getPrice("hello");        System.out.println("Price: " + price);        Future<Double> fp = shop.getPriceAsync("world");        System.out.println("waiting");        try {            price = fp.get();            System.out.println("Price Async: " + price);        } catch (InterruptedException e) {            e.printStackTrace();        } catch (ExecutionException e) {            e.printStackTrace();        }        CompletableFuture.allOf().join();        List<String> ps = shop.findPrices(product);        System.out.println(ps);    }}