package com.chapter11future;/** * Created by wangwenchao02 on 2017/8/28. */public class Quote {    private final String shopName;    private final double price;    private final Discount discount;    public Quote(String shopName, double price, Discount discount) {        this.shopName = shopName;        this.price = price;        this.discount = discount;    }    public static Quote parse(String s) {        String[] split = s.split(":");        String shopName = split[0];        double price = Double.parseDouble(split[1]);        Discount d = Discount.valueOf(split[2]);        return new Quote(shopName, price, d);    }    public String getShopName() {        return shopName;    }    public double getPrice() {        return price;    }    public Discount getDiscount() {        return discount;    }}