package com.chapter12localdate;import java.time.LocalDate;import java.time.LocalTime;/** * Created by wangwenchao02 on 2017/8/28. */public class LocalDateTime {    public static void main(String[] args) {        LocalDate ld = LocalDate.now();        LocalTime lt = LocalTime.now();        System.out.println(ld.atTime(lt));        System.out.println(lt.atDate(ld));    }}