package com.chapter12localdate;import java.time.Instant;import java.time.temporal.ChronoField;/** * Created by wangwenchao02 on 2017/8/28. */public class TestInstant {    public static void main(String[] args) {        System.out.println(Instant.EPOCH);        System.out.println(Instant.ofEpochSecond(999_999_999));        System.out.println(Instant.now());        System.out.println(Instant.now().get(ChronoField.MILLI_OF_SECOND));    }}