package com.chapter12localdate;import java.time.LocalTime;/** * Created by wangwenchao02 on 2017/8/28. */public class TestLocalTime {    public static void main(String[] args) {        System.out.println(LocalTime.MAX);        System.out.println(LocalTime.MIDNIGHT);        System.out.println(LocalTime.MIN);        System.out.println(LocalTime.NOON);        System.out.println(LocalTime.now());        System.out.println(LocalTime.of(20, 30));        System.out.println(LocalTime.ofNanoOfDay(90000000));        System.out.println(LocalTime.ofSecondOfDay(50000));        System.out.println(LocalTime.parse("13:24:35"));    }}