package com.chapter5stream;import java.util.ArrayList;import java.util.List;import java.util.Random;import java.util.stream.Collectors;import java.util.stream.Stream;/** * Created by wangwenchao02 on 2017/8/21. */public class FiboStream {    public static void main(String[] args) {        List<int[]> arr = new ArrayList<>();        arr = Stream.iterate(new int[]{0,1}, t -> new int[]{t[1], t[0]+t[1]}).limit(10).collect(Collectors.toList());        randStream();    }    public static void randStream() {        Stream.generate(() -> new Random().nextInt(100))                .limit(10)                .forEach(System.out::println);    }}