package com.chapter5stream;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;import java.util.stream.IntStream;/** * Created by wangwenchao02 on 2017/8/21. */public class TripleIntStream {    public static void main(String[] args) {        List<int[]> arr = new ArrayList<>();        arr = tripleList(1, 100);        System.out.println(arr);    }    private static List<int[]> tripleList(int start, int end) {        return IntStream.rangeClosed(start, end)                .boxed()                .flatMap(a -> IntStream.rangeClosed(a, end)                                    .filter(b -> Math.sqrt(a*a + b*b) % 1 == 0)                                    .mapToObj(b -> new int[]{a, b, (int)Math.sqrt(a*a + b*b)}))                .collect(Collectors.toList());    }}