package com.chapter7parallel;import java.util.stream.IntStream;import java.util.stream.LongStream;/** * Created by wangwenchao02 on 2017/8/22. */public class ParallelStreams {    public static long sequentialSum(long n) {        return LongStream.rangeClosed(1, n)                .sum();    }    public static long parallelSum(long n) {        return LongStream.rangeClosed(1, n)                .parallel()                .sum();    }}